changeSet.sql:
CREATE TABLE IF NOT EXISTS {{schema}}.profile ( id BIGSERIAL PRIMARY KEY NOT NULL, name VARCHAR(50) UNIQUE NOT NULL, balance DECIMAL(12,2) NOT NULL, status VARCHAR(20) NOT NULL );
CREATE TABLE IF NOT EXISTS {{schema}}.source ( id BIGSERIAL PRIMARY KEY NOT NULL, profile_id BIGINT NOT NULL, name VARCHAR(50) NOT NULL, status VARCHAR(20) NOT NULL,
CONSTRAINT fk_profile FOREIGN KEY (profile_id) REFERENCES {{schema}}.profile (id) );
CREATE TABLE IF NOT EXISTS {{schema}}.translation ( id BIGSERIAL PRIMARY KEY NOT NULL, source_id BIGINT NOT NULL, type VARCHAR(20) NOT NULL, description VARCHAR(255) NOT NULL, to_description VARCHAR(255) NOT NULL, status VARCHAR(20) NOT NULL,
CONSTRAINT fk_source FOREIGN KEY (source_id) REFERENCES {{schema}}.source (id) );
CREATE TABLE IF NOT EXISTS {{schema}}.transaction ( id BIGSERIAL PRIMARY KEY NOT NULL, source_id BIGINT NOT NULL, translation_id BIGINT, type VARCHAR(20) NOT NULL, description VARCHAR(255) NOT NULL, balance DECIMAL(12,2) NOT NULL, input_date TIMESTAMP NOT NULL,
CONSTRAINT fk_source FOREIGN KEY (source_id) REFERENCES {{schema}}.source (id), CONSTRAINT fk_translation FOREIGN KEY (translation_id) REFERENCES {{schema}}.translation (id) );
