changeSet.sql:
CREATE TABLE IF NOT EXISTS ez_schema.profile ( id BIGSERIAL PRIMARY KEY NOT NULL, name VARCHAR(50) UNIQUE NOT NULL, balance DECIMAL(12,2) NOT NULL, status VARCHAR(20) NOT NULL );
CREATE TABLE IF NOT EXISTS ez_schema.translation ( id BIGSERIAL PRIMARY KEY NOT NULL, description VARCHAR(255) NOT NULL, to_description VARCHAR(255) NOT NULL, status VARCHAR(20) NOT NULL );
CREATE TABLE IF NOT EXISTS ez_schema.type ( id BIGSERIAL PRIMARY KEY NOT NULL, name VARCHAR(50) NOT NULL, status VARCHAR(20) NOT NULL );
CREATE TABLE IF NOT EXISTS ez_schema.account ( id BIGSERIAL PRIMARY KEY NOT NULL, profile_id BIGINT NOT NULL, translation_id BIGINT, description VARCHAR(255) NOT NULL, type_id BIGINT NOT NULL, balance DECIMAL(12,2) NOT NULL, input_date TIMESTAMP NOT NULL,
CONSTRAINT fk_profile FOREIGN KEY (profile_id) REFERENCES ez_schema.profile (id), CONSTRAINT fk_translation FOREIGN KEY (translation_id) REFERENCES ez_schema.translation (id), CONSTRAINT fk_type FOREIGN KEY (type_id) REFERENCES ez_schema.type (id) );
